import React, { Component } from "react";
import { Menu, Button } from "antd";
import p1_img from '../../assets/img/logo.png'
import 'antd/dist/antd.css';
import {Link} from 'react-router-dom'
import navData from '../../utils/meun'

const { SubMenu } = Menu;

export default class LeftNav extends Component { 

  render() {
    return (
      <div>
        <div className='nav_left_header'>
            <img className='p1_img' src={p1_img} alt='图片加载失败'/>
            <h2>硅谷后台</h2>
        </div>
        <div>
          
          <Menu
            defaultSelectedKeys={["1"]} //默认选中项
            // defaultOpenKeys={["sub1"]} 默认展开哪一个
            mode="inline"
            theme="dark"
          >

            {
                this.navData.map(((item)=>{
                    
                    if(!item.children.length) {
                        return (
                            <Menu.Item key={item.id} icon={item.icon}>
                              <Link to={item.path}>
                                {item.text}
                              </Link>
                            </Menu.Item>
                        )
                    } else {
                        return (
                            <SubMenu key={item.id} title={item.text} icon={item.icon}>
                                {
                                    item.children.map((d)=>{
                                        return (
                                            <Menu.Item key={d.id} icon={d.icon}>
                                              <Link to={d.path}>
                                                {d.text}
                                              </Link>
                                            </Menu.Item>
                                        )
                                    })
                                }
                            </SubMenu>
                        )
                    }
                }))
            }

          </Menu>
        </div>
      </div>
    );
  }
}
