export const setUser = (data) => {
    localStorage.setItem(data,JSON.stringify(data))
};
export const getUser = (data) => {
    return localStorage.getItem(JSON.parse(data))
}
export const removeUser = (data) => {
    localStorage.removeItem(data)
}
