// 封装axios
import axios from 'axios'

export default async function axios_(url,params={},methods='get') {
    // axios.interceptors.request.use(function (config) {
        
    //     return config;
    // }, function (error) {
    // // 对请求错误做些什么
    //     return Promise.reject(error);
    // });
    console.log()
    if(methods === 'get'){
        return await axios.methods(url,{params})
    } else {
        return await axios.methods(url,params)
    }
    
}
