import React, { Component } from "react";
import { Menu, Button } from "antd";
import {
  AppstoreOutlined,
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  PieChartOutlined,
  DesktopOutlined,
  ContainerOutlined,
  MailOutlined,
} from "@ant-design/icons";
import p1_img from '../../assets/img/logo.png'
import 'antd/dist/antd.css';

const { SubMenu } = Menu;

export default class LeftNav extends Component {
  state = {
    collapsed: true,
  };

