import axios from './ajax'
import jsonp from 'jsonp'
import { message } from 'antd';

export const reqLogin = (params={}) =>  axios('/api/login',params,'post') //登录接口
export const reqGetWeb = (city) => {
    
    new Promise((resolve,resject)=>{
        let url = `http://api.map.baidu.com/telematics/v3/weather?location=${city}&output=jsonp&ak=3p49MVra6urFRGOT9s8UBWr2`
        jsonp(url,{},(err,data)=>{
            console.log(err,data)
            
        })
        if(!err && data) {
            resolve(data)
        } else {
            message.error('获取天气信息失败!')
        }
    })
}
