import React, { Component } from "react";
import { Menu, Button } from "antd";
import p1_img from '../../assets/img/logo.png'
import 'antd/dist/antd.css';
import {Link} from 'react-router-dom'
import navData from '../../config/meun'

const { SubMenu,Item } = Menu;

export default class LeftNav extends Component { 

  //
  getMeun = (arr) => {
     return arr.map((item)=>{
      if(item.children?!item.children.length:true){
        return (
          <Item key={item.id} icon={item.icon}>
            <Link to={item.path}>
              {item.text}
            </Link>
          </Item>
        )
      } else {
        return (
          <SubMenu key={item.id} title={item.text}>
              {this.getMeun(item.children)}
          </SubMenu>
        )
      }
    })
  }

  render() {
    return (
      <div>
        <div className='nav_left_header'>
            <img className='p1_img' src={p1_img} alt='图片加载失败'/>
            <h2>硅谷后台</h2>
        </div>
        <div>
          
          <Menu
            defaultSelectedKeys={["1"]} //默认选中项
            // defaultOpenKeys={["sub1"]} 默认展开哪一个
            mode="inline"
            theme="dark"
          >

            {
               this.getMeun(navData)
                // navData.map(((item)=>{
                    
