export const setUser = (str,data) => {
    localStorage.setItem(str,JSON.stringify(data))
};
export const getUser = (str) => {
    return localStorage.getItem(JSON.parse)
}
export const removeUser = (data) => {
    localStorage.removeItem(data)
}
