import React, { Component } from "react";
import { Menu, Button,Icon } from "antd";
import {
  AppstoreOutlined,
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  PieChartOutlined,
  DesktopOutlined,
  ContainerOutlined,
  MailOutlined,

  HomeOutlined,

} from "@ant-design/icons";
import p1_img from '../../assets/img/logo.png'
import 'antd/dist/antd.css';

const { SubMenu } = Menu;

export default class LeftNav extends Component {
  navData = [
      {
          id: '1',
          text: '首页',
          icon: HomeOutlined,
          children: []
      },{
        id: '2',
        text: '商品',
        icon: HomeOutlined,
        children: []
      },{
        id: '3',
        text: '更多',
        children: [
            {
                id: '3-1',
                text: '平果',
                icon: HomeOutlined,
            },{
                id: '3-2',
                text: '雪梨',
                icon: HomeOutlined,
            },{
                id: '3-3',
                text: '荔枝',
                icon: HomeOutlined,
            }
        ]
      }
  ]

  render() {
    return (
      <div>
        <div className='nav_left_header'>
            <img className='p1_img' src={p1_img} alt='图片加载失败'/>
            <h2>硅谷后台</h2>
        </div>
        <div>
          
          <Menu
            defaultSelectedKeys={["1"]} //默认选中项
            // defaultOpenKeys={["sub1"]} 默认展开哪一个
            mode="inline"
            theme="dark"
          >
            {/* <Menu.Item key="1" icon={<PieChartOutlined />}>
              
            </Menu.Item>
            <Menu.Item key="2" icon={<DesktopOutlined />}>
              Option 2
            </Menu.Item>
            <Menu.Item key="3" icon={<ContainerOutlined />}>
              Option 3
            </Menu.Item>
            <SubMenu key="sub1" icon={<MailOutlined />} title="Navigation One">
              <Menu.Item key="5">Option 5</Menu.Item>
              <Menu.Item key="6">Option 6</Menu.Item>
              <Menu.Item key="7">Option 7</Menu.Item>
              <Menu.Item key="8">Option 8</Menu.Item>
            </SubMenu>
            <SubMenu
              key="sub2"
              icon={<AppstoreOutlined />}
              title="Navigation Two"
            >
              <Menu.Item key="9">Option 9</Menu.Item>
              <Menu.Item key="10">Option 10</Menu.Item>
              <SubMenu key="sub3" title="Submenu">
                <Menu.Item key="11">Option 11</Menu.Item>
                <Menu.Item key="12">Option 12</Menu.Item>
              </SubMenu>
            </SubMenu> */}

            {
                this.navData.map(((item)=>{
                    let icon_ = React.createElement(
                        Icon[item.icon]
                      )

                    if(!item.children.length) {
                        return (
                            <Menu.Item key={item.id} icon={icon_}>
                                {item.text}
                            </Menu.Item>
                        )
                    } else {
                        return (
                            <SubMenu key={item.id} title={item.text} >
                                {
                                    item.children.map((d)=>{
                                        return (
                                            <Menu.Item key={d.id}>
                                                {d.text}
                                            </Menu.Item>
                                        )
                                    })
                                }
                            </SubMenu>
                        )
                    }
                }))
            }

          </Menu>
        </div>
      </div>
    );
  }
}
